<h1>Products</h1>

<div *ngFor="let product of products" class="products">
  <h3 style="cursor:pointer">
    <a
      [title]="product.name + ' details'"
      [href]="product.link"
    >
      {{ product.name }}
    </a>
  </h3>

  <div>
    <img [src]="product.image" style="transform: scale(0.2);">
  </div>

  <button [routerLink]="['/products', product.id]" id="show-details">Show Details</button>

  <button (click)="share()">Share</button>

  <app-product-alerts [product]="product" (click)="onNotify()">
  </app-product-alerts>
</div>

