<div *ngIf="category">
    <h1 class="title">{{category.title}}</h1>

    <div *ngFor="let product of category.items">
        <app-product-item [product]="product" (remove)="removeProduct($event)" (show)="showDetails($event)"></app-product-item>
    </div>
</div>

