import {Component, OnInit} from '@angular/core';

import {ActivatedRoute} from '@angular/router';

import {Product} from '../products';

import {CartService} from '../cart.service';
import {categories} from "../categories";

@Component({
  selector: 'app-product-details',
  templateUrl: './product-details.component.html',
  styleUrls: ['./product-details.component.css']
})

export class ProductDetailsComponent implements OnInit{
  categories = categories;
  currProducts = [];

  product : Product | undefined;

  constructor(private route : ActivatedRoute, 
              private cartService: CartService){

    const routeCatParams = this.route.snapshot.paramMap;
    const categoryIdFromRoute = Number(routeCatParams.get('categoryId'));
    for(let i=0; i<categories.length; i++){
      // @ts-ignore
      if(categories[i].id == categoryIdFromRoute){
        // @ts-ignore
        this.currProducts = categories[i].items;
        break;
      }
    }
  }

  addToCart(product: Product) {
    this.cartService.addToCart(product);
    window.alert('Your product has been added to the cart!');
  }

  ngOnInit(): void {
    const routeParams = this.route.snapshot.paramMap;
    const productIdFromRoute = Number(routeParams.get('productId'));

    // @ts-ignore
    this.product = this.currProducts.find(product => product.id === productIdFromRoute);
  }
}
<h2>Product Details</h2>

<div *ngIf="product">
  <h3>{{ product.name }}</h3>
  <h4>{{ product.price | currency: 'KZT' }}</h4>
  <p class="description">{{ product.description }}</p>
  <button (click)="addToCart(product)">Buy</button>
</div>
