import {Component, EventEmitter, Input, Output} from '@angular/core';
import {Product} from "../products";

@Component({
  selector: 'app-product-item',
  templateUrl: './product-item.component.html',
  styleUrls: ['./product-item.component.css']
})
export class ProductItemComponent {
  @Input() product : { description: string | undefined; id: number | undefined; image: string | undefined; link: string | undefined; name: string | undefined; price: number | undefined; rating: number | undefined };
  @Output() remove = new EventEmitter();

  like : number;
  constructor() {
    this.like = 0;

    this.product = new class implements Product {
      // @ts-ignore
      id: number | undefined;
      // @ts-ignore
      name: string | undefined;
      // @ts-ignore
      image: string | undefined;
      // @ts-ignore
      price: number | undefined;
      // @ts-ignore
      rating: number | undefined;
      // @ts-ignore
      description: string | undefined;
      // @ts-ignore
      link: string | undefined;
    }
  }

  incLike(){
    this.like++;
  }
  removeProduct(){
    this.remove.emit(this.product.id)
  }
  shareViaTelegram() {
    window.open("https://telegram.me/share/url?url=" + this.product.link + "&text=<{{product.name}}>")
  }
  shareViaWhatsapp() {
    window.open("https://wa.me/?text=" + this.product.link)
  }
}
<h3 style="cursor:pointer">
    <a
            [title]="product.name + ' - go to store and make a purchase'"
            [href]="product"
    >
        {{ product.name }}
    </a>
</h3>

<div style="display: flex; flex-direction: row;">
    <img [src]="product.image" alt="product-images" class="product">
    <p style="color:cadetblue; font-style: oblique; margin-left: 100px">Rating : {{product.rating}}</p>

    <div style="display: flex; flex-direction: column">
        <button class="like-button" (click)="incLike()">
            <img src="https://encrypted-tbn0.gstatic.com/images?q=tbn:ANd9GcQPZRn8W46wBbAqtivFdHqRgZqVpkgcnbibUxevuI2oWD_a_76riZiSm70G3_dWe_vk4q4&usqp=CAU" alt="Like button" class="like-button">
        </button>
        <p class="like-cnt" *ngIf="like > 0">{{like}}</p>
    </div>

</div>

<div class="show-and-share-and-remove">
    <button id="show-details">Show Details</button>
    <app-product-details [detailedProduct]="product"></app-product-details>

    <button id="remove" (click)="removeProduct()">Remove</button>

    <div style="display: flex; flex-direction: row; margin-bottom: 20px;">

        <button (click)="shareViaTelegram()" class="icon-buttons">
            <img class="icon" src="data:image/png;base64,">
        </button>

        <button (click)="shareViaWhatsapp()" class="icon-buttons">
            <img class="icon" src="data:image/png;base64,">
        </button>

    </div>

</div>

<div class="line"></div>
